function [fitresult, gof] = createFit(xi, f)
%CREATEFIT(XI,F)
%  Create a fit.
%
%  Data for 'untitled fit 1' fit:
%      X Input : xi
%      Y Output: f
%  Output:
%      fitresult : a fit object representing the fit.
%      gof : structure with goodness-of fit info.
%
%  See also FIT, CFIT, SFIT.

%  Auto-generated by MATLAB on 16-Mar-2017 15:05:19


%% Fit: 'untitled fit 1'.
[xData, yData] = prepareCurveData( xi, f );

% Set up fittype and options.
%ft = fittype( 'exp1' );
ft = fittype(@(a,x) a*exp(-a*x));
opts = fitoptions( 'Method', 'NonlinearLeastSquares' );
opts.Display = 'Off';
opts.StartPoint = [0];

% Fit model to data.
[fitresult, gof] = fit( xData, yData, ft, opts );

% Plot fit with data.
figure( 'Name', 'wikitrace frequency plot' );
h = plot( fitresult, xData, yData );
legend( h, 'f vs. xi', 'fit found', 'Location', 'NorthEast' );
% Label axes
xlabel xi
ylabel 'frequency occurence'
grid on


